/***
 *     Custom settings Swiper Slider
 */
$(document).ready(function(){
  var swiper = new Swiper('.swiper-container-2', {
    slidesPerView: 2,
    spaceBetween: 40,
    pagination: {
      el: '.swiper-pagination',
      clickable: true,
    },
    autoplay: {
      delay: 3000,
      disableOnInteraction: false,
    },
    breakpoints: {
      436: {
        slidesPerView: 1,
      },
    }
  });

  var swiper = new Swiper('.swiper-container-3', {
    slidesPerView: 3,
    spaceBetween: 40,
    pagination: {
      el: '.swiper-pagination',
      clickable: true,
    },
    autoplay: {
      delay: 3000,
      disableOnInteraction: false,
    },
    breakpoints: {
      436: {
        slidesPerView: 1,
      },
      768: {
        slidesPerView: 2,
      },
    }
  });

  var swiper = new Swiper('.swiper-container-4', {
    slidesPerView: 4,
    spaceBetween: 20,
    pagination: {
      el: '.swiper-pagination',
      clickable: true,
    },
    autoplay: {
      delay: 3000,
      disableOnInteraction: false,
    },
    breakpoints: {
      436: {
        slidesPerView: 1,
      },
      768: {
        slidesPerView: 2,
      },
    }
  });
});
